mapper.insert:
INSERT INTO word
(word, phonetic_british, phonetic_american, speech_british_ossid, speech_american_ossid, comment, creator, create_time)
VALUES(#{word}, #{phoneticBritish}, #{phoneticAmerican}, #{speechBritishOssid}, #{speechAmericanOssid}, #{comment}, #{creator}, NOW());

mapper.update:
update word
set
    update_time = NOW()

        , word = #{word}


        , phonetic_british = #{phoneticBritish}


        , mind_spelling = #{mindSpelling}


        , mind_pronunciation = #{mindPronunciation}


        , mind_stress_position = #{mindStressPosition}


        , mind_word_classes = #{mindWordClasses}


        , mind_meaning = #{mindMeaning}


        , has_link = #{hasLink}


        , stars = #{stars}


        , add_count = #{addCount}


        , comment = #{comment}

WHERE
    uid = #{uid}

mapper.insert:
INSERT INTO word_group
(label, comment, creator, create_time)
VALUES(#{label}, #{comment}, #{creator}, NOW());

INSERT INTO word_group_dtl
(group_id, word_id, comment, creator, create_time)
VALUES(#{groupId}, #{wordId}, #{comment}, #{creator}, NOW());

mapper.select:
select w.uid, w.word, w.phonetic_british, w.phonetic_american, w.speech_british_ossid, w.speech_american_ossid, w.mind_spelling, w.mind_pronunciation, w.mind_stress_position, w.mind_word_classes, w.mind_meaning, w.has_link, w.stars, w.add_count, w.comment, w.creator, w.create_time, w.update_time from word w , word_group_dtl wrd where w.uid = wrd.word_id and wrd.group_id = #{groupId}
and w.word like '%#{condition}%'
ORDER by case when w.update_time is null then w.create_time else w.update_time end desc
