mapper.insert:
INSERT INTO wordlist
(word, phonetic_british, phonetic_american, speech_british_ossid, speech_american_ossid, mind_spelling, mind_pronunciation, mind_stress_position, comment, creator, create_time)
VALUES(#{word}, #{phoneticBritish}, #{phoneticAmerican}, #{speechBritishOssid}, #{speechAmericanOssid}, 0, 0, 0, #{comment}, #{creator}, NOW());

mapper.update:
update wordlist
set
    update_time = NOW()

        , word = #{word}


        , phonetic_british = #{phoneticBritish}


        , mind_spelling = #{mindSpelling}


        , mind_pronunciation = #{mindPronunciation}


        , mind_stress_position = #{mindStressPosition}


        , add_count = #{addCount}


        , comment = #{comment}

WHERE
    uid = #{uid}

mapper.insert:
INSERT INTO wordlist_ref
(label, comment, creator, create_time)
VALUES(#{label}, #{comment}, #{creator}, NOW());

INSERT INTO wordlist_ref_detail
(ref_id, word_id, comment, creator, create_time)
VALUES(#{refId}, #{wordId}, #{comment}, #{creator}, NOW());
