mapper.insert:
INSERT INTO wordlist
(word, phonetic_british, phonetic_american, speech_british_ossid, speech_american_ossid, mind_pronunciation, mind_stress_position, comment, creator, create_time)
VALUES(#{word}, #{phoneticBritish}, #{phoneticAmerican}, #{speechBritishOssid}, #{speechAmericanOssid}, 0, 0, #{comment}, #{creator}, NOW());

mapper.update:
update wordlist
set
    update_time = NOW()

        , word = #{word}


        , phonetic_british = #{phoneticBritish}


        , add_count = #{addCount}


        , comment = #{comment}

WHERE
    uid = #{uid}
