mapper.update:
update writing_category
set
    update_time = NOW()

        , label = #{label}

WHERE
    id = #{id}

mapper.select:
SELECT uid, category_id, title, content, comment, creator, create_time, update_time
FROM writing_sample
WHERE category_id = #{categoryId}

mapper.insert:
INSERT INTO writing_sample
(category_id, title, content, comment, creator, create_time)
VALUES(#{categoryId}, #{title}, #{content}, #{comment}, #{creator}, NOW());

mapper.update:
update writing_sample
set
    update_time = NOW()

    , title = #{title}


    , content = #{content}


    , comment = #{comment}

WHERE
    uid = #{uid}

mapper.insert:
INSERT INTO writing_log
(`operation`, part, category_id, sample_id, creator, log_time)
VALUES(#{operation}, #{part}, #{categoryId}, #{sampleId}, #{creator}, NOW());
